import React, { Component } from "react";
import history from "../config/history";
import "./ToDoItemStyle.css";

class ToDoItem extends Component {
  constructor() {
    super();
    this.state = {
      _id: "",
      title: "",
      description: "",
      doneyet: false
    };
  }

  componentWillReceiveProps(nextProps) {
    if (this.props !== nextProps) {
      this.setState(
        {
          _id: nextProps.todoItem._id,
          title: nextProps.todoItem.title,
          description: nextProps.todoItem.description,
          doneyet: nextProps.todoItem.doneyet
        },
        function() {
          console.log("Child component props recieved", this.state);
          console.log("Child component prop for doneyet", this.state.doneyet);
        }
      );
    }
  }

  getTaskAsText = () => {
    return (
      <div class="task-text">
        <span>{this.props.todoItem.title}</span>
        <span>{this.props.todoItem.description}</span>
      </div>
    );
  };

  getTaskAsEditable = () => {
    return (
      <div class="task-text">
        <input
          type="text"
          name="title"
          placeholder={this.props.todoItem.title}
          onChange={this.handleValueChange}
        />
        <input
          type="text"
          name="description"
          placeholder={this.props.todoItem.description}
          onChange={this.handleValueChange}
        />
      </div>
    );
  };

  getButtonAsUpdateAction = () => {
    return (
      <button class="delete-button" onClick={() => this.sendUpdate()}>
        Update
      </button>
    );
  };

  getButtonAsEditAction = () => {
    return (
      <button class="delete-button" onClick={this.props.handleEditAction}>
        Edit
      </button>
    );
  };

  handleValueChange = event => {
    console.log("\n\n ********************** ");
    console.log("event", event.target.name);
    console.log("event", event.target.value);

    this.setState({ [event.target.name]: event.target.value });
  };

  handleCheckboxChange = event => {
    this.setState({ doneyet: !this.state.doneyet });
    console.log("IS DONE ?", this.state.doneyet);
    this.sendUpdate();
  };

  sendUpdate() {
    console.log("SENDING UPDATE TO PARENT", this.state);
    // this.props.handleUpdateAction(
    //   this.state._id,
    //   this.state.title,
    //   this.state.description,
    //   this.state.doneyet
    // );
  }

  render() {
    let isDoneIcon = "(todo!)";
    if (!this.props.todoItem.doneyet) {
      isDoneIcon = "(done!)";
    }

    let task;
    let actionButton = this.getButtonAsEditAction();

    if (!this.props.todoItem.edit) {
      task = this.getTaskAsText();
    } else {
      task = this.getTaskAsEditable();
      actionButton = this.getButtonAsUpdateAction();
    }

    return (
      <div class="todo-item">
        <div class="task-info">
          <input
            type="checkbox"
            // defaultChecked={false}
            defaultChecked={this.state.doneyet}
            // onChange={this.handleCheckboxChange}
            onChange={this.handleValueChange}
            name="doneyet"
            value=""
          />
          {/* <span onClick={() => this.sendUpdate()}>{isDoneIcon}</span> */}
          <span onClick={() => this.sendUpdate()}>
            {this.state.doneyet.toString()}
          </span>
          {/* <span>{this.props.todoItem._id}</span> */}
        </div>
        {task}
        <div class="task-controls">
          {actionButton}
          <button onClick={() => this.props.handleDeleteAction()}>
            Delete
          </button>
        </div>
      </div>
    );
  }
}

export default ToDoItem;
