import React, { Component } from "react";

class SU extends Component {
  constructor(props) {
    super();
  }

  handleSignup() {
    console.log("POST {signup data}");
  }

  render() {
    if (this.props.show) {
      return (
        <div className="signup-form">
          <input placeholder="email" />
          <input placeholder="password" type="password" />
          <button type="submit" onClick={this.props.post}>
            {" "}
            register
          </button>
        </div>
      );
    } else {
      return null;
    }
  }
}

export default SU;
