  handleDelete = id => {
    // get task id and post it
    const taskId = id;
    axios
      .post(
        "http://localhost:5000/api/tasks/delete/" + taskId,
        {},
        { withCredentials: true }
      )
      .then(res => {
        console.log("** POST /task/create", res);
        this.resetInput();
        this.setState(prevState => ({
          items: [
            ...prevState.items,
            {
              title: this.state.taskTitle,
              description: this.state.taskDescription
            }
          ]
        }));
      })
      .catch(error => {
        console.log("500");
        alert("something went wrong. check your network connection");
      });
  };

  render() {
    console.log("his.props.todoItem", this.props.todoItem);

    const isDoneIcon = "NOT YET";
    if (this.props.todoItem.doneyet) {
      isDoneIcon = "done";
    }

    let div = (
      <div class="todo-item">
        <div>
          <span>{isDoneIcon}</span>
          <span>{this.props.todoItem._id}</span>
        </div>
        <div class="task-text">
          <span>{this.props.todoItem.title}</span>
          <span>{this.props.todoItem.description}</span>
        </div>
        <button
          class="delete-button"
          onClick={() => this.handleDelete(this.props.todoItem._id)}
        >
          delete
        </button>
      </div>
    );

    return div;
  }

