import React, { Component, Route } from "react";
import Login from "./Login";
import ToDoInput from "./ToDoInput";
import ToDoList from "./ToDoList";
import "./App.css";

class App extends Component {
  inputElement = React.createRef();
  constructor() {
    super();
    this.state = {
      items: [],
      currentItem: { text: "", key: "" }
    };
  }

  // ? is e here the event ?
  handleInput = e => {
    console.log("Hello Input");
    const input = e.target.value;
    const currentItem = { text: input, key: Date.now() };
    this.setState({ currentItem });
  };

  addItem = e => {
    e.preventDefault();
    console.log("hello add item");
    const newTask = this.state.currentItem;
    if (newTask.text !== "") {
      console.log("new task to add is", newTask);
      const items = [...this.state.items, newTask];
      this.setState({
        items: items,
        currentItem: { text: "", key: "" }
      });
    }
  };

  deleteTask = key => {
    const filteredTasks = this.state.items.filter(item => {
      return item.key !== key;
    });
    // this.state.items = filteredTasks;
    this.setState({
      items: filteredTasks
    });
  };

  editTask = () => {
    console.log("edit me");
  };

  render() {
    return (
      <div className="App">
        <Route path="/" component={Login} />
        {/* <Login /> */}
      </div>
    );
  }

  // render() {
  //   return (
  //     <div className="App">
  //       <ToDoInput
  //         // pass addItem function as prop to child component
  //         addItem={this.addItem}
  //         inputElement={this.inputElement}
  //         handleInput={this.handleInput}
  //         currentItem={this.state.currentItem}
  //       />
  //       <ToDoList
  //         entries={this.state.items}
  //         deleteTask={this.deleteTask}
  //         editTask={this.editTask}
  //       />
  //     </div>
  //   );
  // }
}

export default App;
