import React, { Component } from "react";
import ToDoItem from "./ToDoItem";
import "./ToDoPageStyle.css";
import axios from "axios";

class ToDoPageComponent extends Component {
  constructor() {
    super();
    this.state = {
      taskTitle: "",
      taskDescription: "",
      items: [
        { _id: "", title: "", description: "", doneyet: false, owner: "" }
      ],
      currentItem: { text: "", key: "" }
    };
  }

  componentDidMount() {
    // fetch("http://localhost:5000/api/loggedin", {
    //   method: "GET",
    //   headers: {
    //     // Accept: "application/json",
    //     "Content-Type": "application/json"
    //   },
    //   credentials: "include"
    // })
    //   .then(response => response.json())
    //   .then(data => {
    //     console.log("data from /loggedin is", data);
    //   });

    axios
      .get("http://localhost:5000/api/tasks", { withCredentials: true })
      .then(res => {
        console.log("** GET ALL TASKS ", res);
        this.setState({ items: res.data });
      })
      .catch(error => {
        console.log("500");
        alert("something went wrong. check your network connection");
      });
  }

  // lifecycle method
  // componentDidUpdate() {
  //   console.log("componentDidUpdate()");
  //   this.props.inputElement.current.focus();
  // }

  handleValueChange = event => {
    this.setState({ [event.target.name]: event.target.value });
  };

  handleTaskSubmit = event => {
    // add to callback
    // this.resetInput();

    const task = {
      _id: null,
      title: this.state.taskTitle,
      description: this.state.taskDescription,
      doneyet: true
    };
    console.log("task", task);
    // axios rest call
    axios
      .post(
        "http://localhost:5000/api/tasks/create",
        {
          title: task.title,
          description: task.description,
          doneyet: task.doneyet
        },
        { withCredentials: true }
      )
      .then(res => {
        axios
          .get("http://localhost:5000/api/tasks", { withCredentials: true })
          .then(res => {
            console.log("** GET ALL TASKS ", res);
            this.setState({ items: res.data });
          })
          .catch(error => {
            console.log("500");
            alert("something went wrong. check your network connection");
          });
      })
      .catch(error => {
        console.log("500");
        alert("something went wrong. check your network connection");
      });

    // console.log("** POST /task/create", res);
    // this.resetInput();
    // this.setState(prevState => ({
    //   items: [
    //     ...prevState.items,
    //     {
    //       title: this.state.taskTitle,
    //       description: this.state.taskDescription
    //     }
    //   ]
    // }));
  };

  resetInput = () => {
    this.taskTitleEntry.value = "";
    this.taskInputEntry.value = "";
  };

  handleDelete = id => {
    // get task id and post it
    const taskId = id;
    console.log("**  DELETE :: taskId", taskId);
    console.log("id", id);
    axios
      .post(
        "http://localhost:5000/api/tasks/delete/" + taskId,
        {},
        { withCredentials: true }
      )
      .then(res => {
        this.resetInput();
        axios
          .get("http://localhost:5000/api/tasks", { withCredentials: true })
          .then(res => {
            console.log("** GET ALL TASKS response is  ", res);
            this.setState({ items: res.data });
          })
          .catch(error => {
            console.log("500");
            alert("something went wrong. check your network connection");
          });

        // console.log("res", res);
        // console.log("** POST /task/create", res);
        // this.resetInput();
        // this.setState(prevState => ({
        //   items: [res.data]
        // }));
      });
    // .catch(error => {
    //   console.log("500");
    //   alert("something went wrong. check your network connection");
    // });
  };

  render() {
    const divStyle = {
      display: "flex",
      "flex-direction": "column",
      width: "100vw"
    };

    const tasks = [];
    this.state.items.map(item => {
      tasks.push(
        // <ToDoItem todoItem={item} />

        <ToDoItem
          todoItem={item}
          handleDelete={() => this.handleDelete(item._id)}
        />
      );
    });

    return (
      <div class="page-container" style={divStyle}>
        <div class="page-title">
          <h2>Tasks ({this.state.items.length})!</h2>
        </div>
        <div class="task-form">
          <input
            name="taskTitle"
            class="task-input"
            placeholder="  + Add Title"
            onChange={this.handleValueChange}
            ref={el => (this.taskTitleEntry = el)}
          />
          <input
            name="taskDescription"
            class="task-input"
            placeholder="  + Add Description"
            onChange={this.handleValueChange}
            ref={el => (this.taskInputEntry = el)}
          />
          <button onClick={this.handleTaskSubmit}> Add Task </button>
        </div>
        <div class="task-list">{tasks}</div>
      </div>
    );
  }

  // render() {
  //   return (
  //     <div className="todoListMain">
  //       <div className="header">
  //         <form onSubmit={this.props.addItem}>
  //           <input
  //             placeholder="Task"
  //             ref={this.props.inputElement}
  //             value={this.props.currentItem.text}
  //             onChange={this.props.handleInput}
  //           />
  //           <button type="submit"> add something todo</button>
  //         </form>
  //       </div>
  //     </div>
  //   );
  // }
}

export default ToDoPageComponent;
