import React, { Component } from "react";
import history from "../config/history";

import "./SignUpStyle.css";
import UserService from "../rest/UserService";

class SignUP extends Component {
  constructor(props) {
    super();

    this.state = {
      username: "",
      password: "",
      hasEnoughCharacters: true
    };
  }

  handlePost() {
    if (this.state.password.length < 7) {
      this.setState({ hasEnoughCharacters: false });
      return;
    } else {
      this.setState({ hasEnoughCharacters: true });
    }

    const userDetails = {
      username: this.state.username,
      password: this.state.password
    };

    UserService.register();

    fetch("http://localhost:5000/api/signup", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        username: this.state.username,
        password: this.state.password
      })
    }).then(res => {
      if (res.status === 200) {
        history.push("/t");
      }
    });
  }

  render() {
    if (this.props.show) {
      return (
        <div className="signup-form">
          <input
            type="text"
            name="Username"
            placeholder="Username"
            value={this.state.username}
            onChange={e => this.setState({ username: e.target.value })}
          />
          <input
            type="password"
            name="password"
            placeholder="Password (At Least 7 Characters)"
            value={this.state.password}
            onChange={e => this.setState({ password: e.target.value })}
          />
          {!this.state.hasEnoughCharacters ? (
            <span>7 characters minium</span>
          ) : null}
          <button type="submit" onClick={() => this.handlePost()}>
            Register
          </button>
        </div>
      );
    } else {
      return null;
    }
  }
}

export default SignUP;
