import UserService from "../rest/UserService";

import "./NavStyle.css";

class NavBar extends Component {
  constructor(props) {
    super();
  }

  handlePost() {
    console.log("POST {signup data}");
  }
  handleLogout = e => {
    console.log("handles logOUT");

