  };

  render() {
    return (
      <nav class="nav-bar">
        IronHack Task List
        <button
          onClick={() => this.handleLogout()}
          style={{ float: "right", margin: "auto" }}
        >
          Logout
        </button>
      </nav>
    );
  }
}

export default NavBar;
