import React, { Component } from "react";
import { Router, Route, BrowserRouter } from "react-router-dom";
import Login from "./Login";
import T1 from "./todo/T1";
import NH from "./nav/NavHeader";
import ToDoInput from "./ToDoInput";
import ToDoList from "./ToDoList";
import "./App.css";

class App extends Component {
  inputElement = React.createRef();
  constructor() {
    super();
    this.state = {
      items: [],
      currentItem: { text: "", key: "" },
      isAuth: true
    };
  }

  handleAuth = e => {
    console.log("handling auth");
  };

  // ? is e here the event ?
  handleInput = e => {
    console.log("Hello Input");
    const input = e.target.value;
    const currentItem = { text: input, key: Date.now() };
    this.setState({ currentItem });
  };

  addItem = e => {
    e.preventDefault();
    console.log("hello add item");
    const newTask = this.state.currentItem;
    if (newTask.text !== "") {
      console.log("new task to add is", newTask);
      const items = [...this.state.items, newTask];
      this.setState({
        items: items,
        currentItem: { text: "", key: "" }
      });
    }
  };

  deleteTask = key => {
    const filteredTasks = this.state.items.filter(item => {
      return item.key !== key;
    });
    // this.state.items = filteredTasks;
    this.setState({
      items: filteredTasks
    });
  };

  editTask = () => {
    console.log("edit me");
  };

  render() {
    let navHeader = this.state.isAuth ? <NH /> : "";
    return (
      <BrowserRouter>
        <div className="App">
          {navHeader}

          <Route
            path="/"
            render={props => <Login {...props} handleLogin={this.handleAuth} />}
          />

          <Route path="/t" component={T1} />
          {/* <Login /> */}
        </div>
      </BrowserRouter>
    );
  }

  //   {/* <Route exact path="/" component={Login} /> */}
  // {/* <Route
  //   exact
  //   path="/"
  //   component={Login}
  //   render={props => <Dashboard {...props} isAuthed={true} />}
  // /> */}

  // render() {
  //   return (
  //     <div className="App">
  //       <ToDoInput
  //         // pass addItem function as prop to child component
  //         addItem={this.addItem}
  //         inputElement={this.inputElement}
  //         handleInput={this.handleInput}
  //         currentItem={this.state.currentItem}
  //       />
  //       <ToDoList
  //         entries={this.state.items}
  //         deleteTask={this.deleteTask}
  //         editTask={this.editTask}
  //       />
  //     </div>
  //   );
  // }
}

export default App;
