import React, { Component } from "react";
import ToDoItem from "./ToDoItem";
import "./ToDoPageStyle.css";

class ToDoPageComponent extends Component {
  constructor() {
    super();
    this.state = {
      items: [],
      currentItem: { text: "", key: "" }
    };
  }

  componentDidMount() {
    fetch("http://localhost:5000/api/loggedin", {
      method: "GET",
      headers: {
        // Accept: "application/json",
        "Content-Type": "application/json"
      },
      credentials: "include"
    })
      .then(response => response.json())
      .then(data => {
        console.log("data from /loggedin is", data);
      });
  }

  // lifecycle method
  // componentDidUpdate() {
  //   console.log("componentDidUpdate()");
  //   this.props.inputElement.current.focus();
  // }

  handleKeyEvent = e => {
    if (e.key === "Enter") {
      // console.log("get input & add task to collection" + e.targer.value);
      const items = this.state.items.concat("cats");
      console.log("items", items);
      this.setState({ items: items });
      console.log(">>> state is ", this.state);
      this.taskInputEntry.value = "";
    }
  };

  render() {
    const divStyle = {
      display: "flex",
      "flex-direction": "column",
      width: "100vw"
    };

    return (
      <div class="page-container" style={divStyle}>
        <div class="page-title">
          <h2>Tasks (6)!</h2>
        </div>
        <div>
          <input
            class="task-input"
            placeholder="  + Add New"
            onKeyPress={this.handleKeyEvent}
            ref={el => (this.taskInputEntry = el)}
          />
        </div>
        <div class="task-list">
          <div class="item" />
          {this.state.items.map((item, index) => {
            <ToDoItem todoItems={this.state.items} />;
          })}
        </div>
      </div>
    );
  }

  // render() {
  //   return (
  //     <div className="todoListMain">
  //       <div className="header">
  //         <form onSubmit={this.props.addItem}>
  //           <input
  //             placeholder="Task"
  //             ref={this.props.inputElement}
  //             value={this.props.currentItem.text}
  //             onChange={this.props.handleInput}
  //           />
  //           <button type="submit"> add something todo</button>
  //         </form>
  //       </div>
  //     </div>
  //   );
  // }
}

export default ToDoPageComponent;
