import React, { Component } from "react";
import history from "../config/history";

class T2 extends Component {
  //   createTasks = item => {
  //     return (
  //       <div>
  //         <li key={item.key} onClick={() => this.props.editTask()}>
  //           {item.text}
  //         </li>
  //         <button onClick={() => this.props.deleteTask(item.key)}>kill me</button>
  //       </div>
  //     );
  //   };
  handleLogout = e => {
    console.log("handles login");

    fetch("http://localhost:5000/api/logout", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      },
      body: JSON.stringify({})
    })
      .then(res => res.json())
      .then(res => {
        console.log("res", res);
        if (res.message === "Success") {
          console.log(">> Logged out <<");
          history.push("/");
        }
      });
  };

  handleMe = e => {
    console.log("handles login");

    // fetch("http://localhost:5000/api/tasks/create", {
    //   method: "POST",
    //   headers: {
    //     Accept: "application/json",
    //     "Content-Type": "application/json"
    //   },
    //   credentials: "include",
    //   body: JSON.stringify({})
    // })
    //   .then(res => res.json())
    //   .then(res => {
    //     console.log("res", res);
    //     if (res.message === "Success") {
    //       console.log(">> Logged out <<");
    //     }
    //   });
  };

  handleA = e => {
    console.log("calling GET : /tasks");

    fetch("http://localhost:5000/api/tasks/", {
      method: "GET",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      },
      credentials: "include"
    })
      .then(res => res.json())
      .then(res => {
        console.log("res ALL THE TASKS ", res);
      });
  };

  handleB = e => {
    console.log("calling POST:  /tasks/create");

    fetch("http://localhost:5000/api/tasks/create", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      },
      credentials: "include",
      body: JSON.stringify({})
    })
      .then(res => res.json())
      .then(res => {
        console.log("res", res);
      });
  };

  handleC = e => {
    console.log("calling POST : /tasks/edit/:id");

    fetch("http://localhost:5000/api/tasks/edit/1", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      },
      credentials: "include",
      body: JSON.stringify({})
    })
      .then(res => res.json())
      .then(res => {
        console.log("res", res);
        if (res.message === "Success") {
          console.log(">> Logged out <<");
        }
      });
  };

  handleD = e => {
    console.log("calling POST : /tasks/delete/:id");

    fetch("http://localhost:5000/api/tasks/delete/1", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      },
      credentials: "include",
      body: JSON.stringify({})
    })
      .then(res => res.json())
      .then(res => {
        console.log("res", res);
        if (res.message === "Success") {
          console.log(">> Logged out <<");
        }
      });
  };

  render() {
    // const toDoEntries = this.props.entries;
    // const listItems = toDoEntries.map(this.createTasks);

    const divStyle = {
      display: "flex",
      "flex-direction": "column",
      width: "20vw",
      margin: "0 auto"
    };

    return (
      <div>
        <ul className="theList">
          <li>test</li>
        </ul>
        <div style={divStyle}>
          <button onClick={() => this.handleLogout()}>logout</button>
          {/* <button onClick={() => this.handleMe()}>/TASKS</button> */}
          <button onClick={() => this.handleA()}>/TASKS</button>
          <button onClick={() => this.handleB()}>/TASKS/CREATE</button>
          <button onClick={() => this.handleC()}>/TASKS/EDIT/:ID</button>
          <button onClick={() => this.handleD()}>/TASKS/DELETE/:ID</button>
        </div>
      </div>
    );
  }
}

export default T2;
