import React, { Component } from "react";
import ToDoItem from "./ToDoItem";
import "./ToDoPageStyle.css";
import axios from "axios";

class ToDoPageComponent extends Component {
  constructor() {
    super();
    this.state = {
      taskTitle: "",
      taskDescription: "",
      tasks: [],
      items: [{ _id: "", title: "", description: "", doneyet: false }],
      currentItem: { text: "", key: "" }
    };
  }

  componentDidMount() {
    axios
      .get("http://localhost:5000/api/tasks", { withCredentials: true })
      .then(res => {
        this.setState({ items: res.data });
      })
      .catch(error => {
        alert("something went wrong. check your network connection");
      });
  }

  handleValueChange = event => {
    this.setState({ [event.target.name]: event.target.value });
  };

  handleTaskSubmit = event => {
    if (this.state.taskTitle.length <= 0 || this.state.taskDescription <= 0) {
      alert("A task requires both a Title and Description");
      return;
    }

    const task = {
      _id: null,
      title: this.state.taskTitle,
      description: this.state.taskDescription,
      doneyet: true
    };

    axios
      .post(
        "http://localhost:5000/api/tasks/create",
        {
          title: task.title,
          description: task.description,
          doneyet: task.doneyet
        },
        { withCredentials: true }
      )
      .then(res => {
        this.resetInput();
        axios
          .get("http://localhost:5000/api/tasks", { withCredentials: true })
          .then(res => {
            this.setState({ items: res.data });
          })
          .catch(error => {
            alert("something went wrong. check your network connection");
          });
      })
      .catch(error => {
        alert("something went wrong. check your network connection");
      });
  };

  resetInput = () => {
    this.taskTitleEntry.value = "";
    this.taskInputEntry.value = "";
    this.setState({ taskTitle: "", taskDescription: "" });
  };

  handleDeleteAction = id => {
    axios
      .post(
        "http://localhost:5000/api/tasks/delete/" + id,
        {},
        { withCredentials: true }
      )
      .then(res => {
        this.resetInput();
        axios
          .get("http://localhost:5000/api/tasks", { withCredentials: true })
          .then(res => {
            this.setState({ items: res.data });
          })
          .catch(error => {
            alert("something went wrong. check your network connection");
          });
      });
  };

  handleEditAction = id => {
    console.log("EDIT TASK WITH ID", id);
  };

  render() {
    const divStyle = {
      display: "flex",
      "flex-direction": "column",
      width: "100vw"
    };

    const tasks = [];
    this.state.items.map(item => {
      tasks.push(
        <ToDoItem
          todoItem={item}
          handleEditAction={() => this.handleEditAction(item._id)}
          handleDeleteAction={() => this.handleDeleteAction(item._id)}
        />
      );
    });

    return (
      <div class="page-container" style={divStyle}>
        <div class="page-title">
          <h2>Tasks ({this.state.items.length})!</h2>
        </div>
        <div class="task-form">
          <input
            name="taskTitle"
            class="task-input"
            placeholder="  + Add Title"
            onChange={this.handleValueChange}
            ref={el => (this.taskTitleEntry = el)}
          />
          <input
            name="taskDescription"
            class="task-input"
            placeholder="  + Add Description"
            onChange={this.handleValueChange}
            ref={el => (this.taskInputEntry = el)}
          />
          <button onClick={this.handleTaskSubmit}> Add Task </button>
        </div>
        <div class="task-list">{tasks}</div>
      </div>
    );
  }
}

export default ToDoPageComponent;
