import React, { Component } from "react";

class SU extends Component {
  constructor(props) {
    super();

    this.state = {
      username: null,
      password: null
    };
  }

  handlePost() {
    console.log("POST {signup data}");

    fetch("http://localhost:5000/api/signup", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        username: "Jon",
        password: "pwd12378"
      })
    }).then(res => {
      console.log("response is ", res.status);
      if (res.status === 200) {
        console.log("welcome home!");
        // this.props.history.push("/t");
      } else {
        console.log("you need to figure that shit out");
      }
    });
  }

  render() {
    if (this.props.show) {
      return (
        <div className="signup-form">
          <input
            placeholder="username"
            ref={this.props.inputElement}
            value={this.props.currentItem.username}
            onChange={this.props.handleInput}
          />
          <input placeholder="password" type="password" />
          <button type="submit" onClick={() => this.handlePost()}>
            register
          </button>
        </div>
      );
    } else {
      return null;
    }
  }
}

export default SU;
