import React, { Component } from "react";
import SU from "./sign-up/SignUp";

import history from "./config/history";

class Login extends Component {
  auth = false;

  showSignUp = false;

  constructor() {
    super();
    this.state = {
      username: "",
      password: "",
      userNotFound: false,
      showSignUp: false
    };
  }
  // lifecycle method
  // componentDidUpdate() {
  //   console.log("componentDidUpdate()");
  //   this.props.inputElement.current.focus();
  // }

  handleLogin = e => {
    console.log("handles login");

    fetch("http://localhost:5000/api/login", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        username: this.state.username,
        password: this.state.password
      })
    }).then(res => {
      if (res.message === "sorry, we coun't find that account") {
        console.log("user credentials NOT SO GOOD");
        this.setState({ userNotFound: true });
      } else {
        // history.push("/t");
        console.log("user credentials GOOD");
      }
    });
  };

  handleSignup = e => {
    this.setState({ showSignUp: true });
    e.preventDefault();
  };

  render() {
    return (
      <div className="todoListMain">
        <div className="header">
          <input
            placeholder="Username"
            name="username"
            type="text"
            value={this.state.username}
            onChange={e => this.setState({ username: e.target.value })}
          />
          <input
            placeholder="Password"
            name="password"
            type="password"
            value={this.state.password}
            onChange={e => this.setState({ password: e.target.value })}
          />
          <button type="submit" onClick={() => this.handleLogin()}>
            LOGIN
          </button>
        </div>
        <div className="header">
          <span>
            If no account register
            <a href="" onClick={this.handleSignup}>
              here
            </a>
          </span>
        </div>
        <div className="header">
          <SU show={this.state.showSignUp} />
        </div>
      </div>
    );
  }
}

export default Login;
