import React, { Component } from "react";
import { Router, Route } from "react-router-dom";
import Login from "./Login";
import ToDoPageComponent from "./todo/ToDoPageComponent";
import NavBar from "./nav/NavHeader";
import ToDoInput from "./ToDoInput";
import ToDoList from "./ToDoList";
import "./App.css";

import history from "./config/history";

class App extends Component {
  inputElement = React.createRef();
  constructor() {
    super();
    this.state = {
      items: [],
      currentItem: { text: "", key: "" },
      isAuth: true
    };
  }

  handleInput = e => {
    console.log("Hello Input");
    const input = e.target.value;
    const currentItem = { text: input, key: Date.now() };
    this.setState({ currentItem });
  };

  addItem = e => {
    e.preventDefault();
    console.log("hello add item");
    const newTask = this.state.currentItem;
    if (newTask.text !== "") {
      console.log("new task to add is", newTask);
      const items = [...this.state.items, newTask];
      this.setState({
        items: items,
        currentItem: { text: "", key: "" }
      });
    }
  };

  deleteTask = key => {
    const filteredTasks = this.state.items.filter(item => {
      return item.key !== key;
    });
    // this.state.items = filteredTasks;
    this.setState({
      items: filteredTasks
    });
  };

  editTask = () => {
    console.log("edit me");
  };

  handleAuth = () => {
    fetch("http://localhost:5000/api/login", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        username: "sex",
        password: "sexy"
      })
    }).then(res => {
      console.log("response is ", res.status);
      if (res.status === 200) {
        console.log("so sexy");
        history.push("/t");
      } else {
        console.log("not so sexy");
      }
    });

    this.setState({ isAuth: true });
  };

  render() {
    let navHeader = this.state.isAuth ? <NavBar /> : "";
    return (
      <Router history={history}>
        <div className="App">
          {navHeader}
          <Route
            exact
            path="/"
            render={props => <Login {...props} handleAuth={this.handleAuth} />}
          />
          <Route path="/tasks" component={ToDoPageComponent} />
        </div>
      </Router>
    );
  }
}

export default App;
