import React, { Component } from "react";
import ToDoItem from "./ToDoItem";
import "./ToDoPageStyle.css";

class ToDoPageComponent extends Component {
  constructor() {
    super();
    this.state = {
      items: [{ title: "", description: "" }],
      currentItem: { text: "", key: "" }
    };
  }

  componentDidMount() {
    fetch("http://localhost:5000/api/loggedin", {
      method: "GET",
      headers: {
        // Accept: "application/json",
        "Content-Type": "application/json"
      },
      credentials: "include"
    })
      .then(response => response.json())
      .then(data => {
        console.log("data from /loggedin is", data);
      });
  }

  // lifecycle method
  // componentDidUpdate() {
  //   console.log("componentDidUpdate()");
  //   this.props.inputElement.current.focus();
  // }

  handleChange = event => {
    console.log("input");
    console.log("title", event.target.title);
    console.log("desc", event.target.description);
    this.setState({ [event.target.name]: event.target.value });
    // this.setState({
    //   title: event.target.title,
    //   description: event.target.desc
    // });
  };

  handleTaskSubmit = e => {
    this.setState(prevState => ({
      items: [...prevState.items, this.state.items.pop]
    }));
    this.resetInput();
  };

  resetInput = () => {
    this.taskTitleEntry.value = "";
    this.taskInputEntry.value = "";
  };

  render() {
    const divStyle = {
      display: "flex",
      "flex-direction": "column",
      width: "100vw"
    };

    const tasks = [];
    this.state.items.map(item => {
      tasks.push(<ToDoItem todoItem={item} />);
    });

    return (
      <div class="page-container" style={divStyle}>
        <div class="page-title">
          <h2>Tasks ({this.state.items.length})!</h2>
        </div>
        <div class="task-form">
          <input
            name="title"
            class="task-input"
            placeholder="  + Add Title"
            onChange={this.handleChange}
            ref={el => (this.taskTitleEntry = el)}
          />
          <input
            name="description"
            class="task-input"
            placeholder="  + Add Description"
            onChange={this.handleChange}
            ref={el => (this.taskInputEntry = el)}
          />
          <button onClick={this.handleTaskSubmit}> Add Task </button>
        </div>
        <div class="task-list">{tasks}</div>
      </div>
    );
  }

  // render() {
  //   return (
  //     <div className="todoListMain">
  //       <div className="header">
  //         <form onSubmit={this.props.addItem}>
  //           <input
  //             placeholder="Task"
  //             ref={this.props.inputElement}
  //             value={this.props.currentItem.text}
  //             onChange={this.props.handleInput}
  //           />
  //           <button type="submit"> add something todo</button>
  //         </form>
  //       </div>
  //     </div>
  //   );
  // }
}

export default ToDoPageComponent;
