import React, { Component } from "react";
import history from "../config/history";
import "./ToDoItemStyle.css";

class ToDoItem extends Component {
  constructor() {
    super();
    this.state = {
      title: "",
      description: ""
    };
  }

  handleLogout = e => {
    fetch("http://localhost:5000/api/logout", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      },
      body: JSON.stringify({})
    })
      .then(res => res.json())
      .then(res => {
        console.log("res", res);
        if (res.message === "Success") {
          console.log(">> Logged out <<");
          history.push("/");
        }
      });
  };

  getTaskAsText = () => {
    return (
      <div class="task-text">
        <span>{this.props.todoItem.title}</span>
        <span>{this.props.todoItem.description}</span>
      </div>
    );
  };

  handleValueChange = event => {
    console.log("VALUE CHANGE FOR EDIT TASK");
    this.setState({ [event.target.name]: event.target.value });
  };

  getTaskAsEditable = () => {
    return (
      <div class="task-text">
        <input
          type="text"
          name="title"
          placeholder={this.props.todoItem.title}
          onChange={this.handleValueChange}
        />
        <input
          type="text"
          name="description"
          placeholder={this.props.todoItem.description}
          onChange={this.handleValueChange}
        />
      </div>
    );
  };

  getButtonAsEditAction = () => {
    return (
      <button class="delete-button" onClick={this.props.handleEditAction}>
        edit
      </button>
    );
  };

  getButtonAsUpdateAction = () => {
    return (
      <button
        class="delete-button"
        onClick={() =>
          this.props.handleUpdateAction(
            this.state.title,
            this.state.description
          )
        }
      >
        Update
      </button>
    );
  };

  render() {
    console.log("his.props.todoItem", this.props.todoItem);

    let isDoneIcon = "NOT YET";
    // if (this.props.todoItem.doneyet) {
    //   isDoneIcon = "done";
    // }

    if (this.props.todoItem.edit) {
      isDoneIcon = "EDIT ME";
    }

    let task;
    let actionButton = this.getButtonAsEditAction();

    if (!this.props.todoItem.edit) {
      task = this.getTaskAsText();
    } else {
      task = this.getTaskAsEditable();
      actionButton = this.getButtonAsUpdateAction();
    }

    let div = (
      <div class="todo-item">
        <div class="task-info">
          <span>{isDoneIcon}</span>
          <span>{this.props.todoItem._id}</span>
        </div>
        {task}
        <div class="task-controls">
          {actionButton}
          <button class="delete-button" onClick={this.props.handleDeleteAction}>
            delete
          </button>
        </div>
      </div>
    );

    return div;
  }

  // render() {
  //   // const toDoEntries = this.props.entries;
  //   // const listItems = toDoEntries.map(this.createTasks);

  //   const divStyle = {
  //     display: "flex",
  //     "flex-direction": "column",
  //     width: "20vw",
  //     margin: "0 auto"
  //   };

  //   return (
  //     <div>
  //       <ul className="theList">
  //         <li>test</li>
  //       </ul>
  //       <div style={divStyle}>
  //         <button onClick={() => this.handleLogout()}>logout</button>
  //         {/* <button onClick={() => this.handleMe()}>/TASKS</button> */}
  //         <button onClick={() => this.handleA()}>/TASKS</button>
  //         <button onClick={() => this.handleB()}>/TASKS/CREATE</button>
  //         <button onClick={() => this.handleC()}>/TASKS/EDIT/:ID</button>
  //         <button onClick={() => this.handleD()}>/TASKS/DELETE/:ID</button>
  //       </div>
  //     </div>
  //   );
  // }
}

export default ToDoItem;
