import React, { Component } from "react";

import history from "../config/history";

class SU extends Component {
  constructor(props) {
    super();

    this.state = {
      username: "",
      password: "",
      hasEnoughCharacters: true
    };
  }

  handlePost() {
    if (this.state.password.length < 7) {
      this.setState({ hasEnoughCharacters: false });
      return;
    } else {
      this.setState({ hasEnoughCharacters: true });
    }
    console.log("POST {signup data}", this.state);
    const uname = this.state.username;
    const passwd = this.state.password;

    fetch("http://localhost:5000/api/signup", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        username: uname,
        password: passwd
      })
    }).then(res => {
      if (res.status === 200) {
        history.push("/t");
        // this.props.history.push("/t");
      } else {
        console.log("you need to figure that shit out");
      }
    });
  }

  render() {
    let warning;
    if (!this.state.hasEnoughCharacters) {
      warning = <span>7 characters minium</span>;
    }
    if (this.props.show) {
      return (
        <div className="signup-form">
          <input
            type="text"
            name="Username"
            placeholder="username"
            value={this.state.username}
            onChange={e => this.setState({ username: e.target.value })}
          />
          <input
            type="password"
            name="password"
            placeholder="Password (At Least 7 Characters)"
            value={this.state.password}
            onChange={e => this.setState({ password: e.target.value })}
          />
          {warning}
          <button type="submit" onClick={() => this.handlePost()}>
            Register
          </button>
        </div>
      );
    } else {
      return null;
    }
  }
}

export default SU;
