import React, { Component } from "react";

class SU extends Component {
  constructor(props) {
    super();

    this.state = {
      username: "",
      password: ""
    };
  }

  // componentDidUpdate() {
  //   console.log("componentDidUpdate()");
  //   this.props.inputElement.current.focus();
  // }

  handlePost() {
    console.log("POST {signup data}");

    fetch("http://localhost:5000/api/signup", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        username: "Jon",
        password: "pwd12378"
      })
    }).then(res => {
      console.log("response is ", res.status);
      if (res.status === 200) {
        console.log("welcome home!");
        // this.props.history.push("/t");
      } else {
        console.log("you need to figure that shit out");
      }
    });
  }

  // updateInputValue(evt) {
  //   console.log("inputvalue changed");
  //   this.setState({
  //     inputValue: evt.target.value
  //   });
  // }

  handleInputChange(event) {
    console.log("inputvalue changed", event);

    // const name = target;
    // this.setState({
    //   inputValue: evt.target.value
    // });
  }

  render() {
    if (this.props.show) {
      return (
        <div className="signup-form">
          <input
            type="text"
            name="username"
            placeholder="username"
            value={this.state.username}
            onChange={this.handleInputChange}
          />

          <input
            type="password"
            name="password"
            placeholder="password"
            value={this.state.password}
            onChange={this.handleInputChange}
          />
          <button type="submit" onClick={() => this.handlePost()}>
            register
          </button>
        </div>
      );
    } else {
      return null;
    }
  }
}

export default SU;
