import React, { Component } from "react";
import ToDoItem from "./ToDoItem";
import "./ToDoPageStyle.css";

class ToDoPageComponent extends Component {
  constructor() {
    super();
    this.state = {
      items: ["cats"],
      currentItem: { text: "", key: "" }
    };
  }

  componentDidMount() {
    fetch("http://localhost:5000/api/loggedin", {
      method: "GET",
      headers: {
        // Accept: "application/json",
        "Content-Type": "application/json"
      },
      credentials: "include"
    })
      .then(response => response.json())
      .then(data => {
        console.log("data from /loggedin is", data);
      });
  }

  // lifecycle method
  // componentDidUpdate() {
  //   console.log("componentDidUpdate()");
  //   this.props.inputElement.current.focus();
  // }

  handleKeyEvent = e => {
    const items = this.state.items.concat("cats");
    console.log("items", items);
    this.setState({ items: items });
    console.log(">>> state is ", this.state);
    this.taskTitleEntry.value = "";
    this.taskInputEntry.value = "";

    if (e.key === "Enter") {
      // console.log("get input & add task to collection" + e.targer.value);
      const items = this.state.items.concat("cats");
      console.log("items", items);
      this.setState({ items: items });
      console.log(">>> state is ", this.state);
      this.taskInputEntry.value = "";
    }
  };

  render() {
    const divStyle = {
      display: "flex",
      "flex-direction": "column",
      width: "100vw"
    };

    const tasks = [];
    this.state.items.map(item => {
      tasks.push(<ToDoItem todoItem={item} />);
    });

    return (
      <div class="page-container" style={divStyle}>
        <div class="page-title">
          <h2>Tasks ({this.state.items.length})!</h2>
        </div>
        <div class="task-form">
          <input
            class="task-input"
            placeholder="  + Add Title"
            ref={el => (this.taskTitleEntry = el)}
          />
          <input
            class="task-input"
            placeholder="  + Add Description"
            ref={el => (this.taskInputEntry = el)}
          />
          <button onClick={this.handleKeyEvent}> Add Task </button>
        </div>
        <div class="task-list">{tasks}</div>
      </div>
    );
  }

  // render() {
